import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import MenuItem from "@mui/material/MenuItem";

import Navbar from "./components/Navbar";
import axios from "axios";

const Transaction = () => {
  const expenseCategory = [
    {
      value: "Food",
      label: "$",
    },
    {
      value: "Transport",
      label: "€",
    },
  ];
  const incomeCategory = [
    {
      value: "Salary",
      label: "$",
    },
    {
      value: "Other Income",
      label: "€",
    },
  ];
  const types = [
    {
      value: "Income",
      label: "+",
    },
    {
      value: "Expense",
      label: "-",
    },
  ];

  const [type, setType] = useState("Income");
  const [category, setCategory] = useState("");
  const [amount, setAmount] = useState(0);
  const [note, setNote] = useState("");
  const [date, setDate] = useState("");
  const [trasactionForm,setTrasactionForm] = useState 
  // console.log(new Date().toLocaleDateString("en-GB"));
  const handleTypeChange = (event) => {
    setType(event.target.value);
  };
  const handleCategoryChange = (event) => {
    setCategory(event.target.value);
  };
  const handleAmountChange = (event) => {
    setAmount(event.target.value);
  };
  const handleNoteChange = (event) => {
    setNote(event.target.value);
  };
  const handleDateChange = (event) => {
    // const newDate = event.target.value.split("-").reverse().join("/");
    setDate(event.target.value);
    // console.log(date);
  };
  const handleCancel = () => {
    setType("Income");
    setCategory("");
    setAmount(0);
    setNote("");
    setDate("");
  };

  const handleSave = (event) => {
    event.preventDefault();
    const data = JSON.parse(localStorage.getItem("loginData"));
    const transaction = {
      id: localStorage.getItem("id"),
      walletname: data.wallet_name,
      type: type,
      category: category,
      amount: amount,
      note: note,
      date: date,
    };
    console.log(transaction);
    axios
      .post("http://127.0.0.1:5000/transactions", transaction)
      .then(function (response) {
        console.log(response.data);

        console.log(response.data.message);
      })
      .catch(function (error) {
        console.log(error);
      });
      handleCancel();
  };

  return (
    <>
      <Navbar/>
      <div className="container mt-5">
      <button type="button" class="btn btn-primary">Add Transaction</button>
      {}
        <TextField
          id="outlined-select-currency"
          select
          label="Type"
          helperText="Please select your type"
          onChange={handleTypeChange}
          value={type}
        >
          {types.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label} {option.value}
            </MenuItem>
          ))}
        </TextField>

        {type === "Income" ? (
          <TextField
            id="outlined-select-currency"
            select
            label="Category"
            helperText="Please select your category"
            value={category}
            onChange={handleCategoryChange}
          >
            {incomeCategory.map((option) => (
              <MenuItem key={option.value} value={option.value}>
                {option.label} {option.value}
              </MenuItem>
            ))}
          </TextField>
        ) : (
          <TextField
            id="outlined-select-currency"
            select
            label="Category"
            helperText="Please select your category"
            value={category}
            onChange={handleCategoryChange}
          >
            {expenseCategory.map((option) => (
              <MenuItem key={option.value} value={option.value}>
                {option.label} {option.value}
              </MenuItem>
            ))}
          </TextField>
        )}

        <TextField
          id="outlined-basic"
          label="Amount"
          value={amount}
          onChange={handleAmountChange}
          variant="outlined"
        />
        <TextField
          fullWidth
          label="Note"
          id="outlined-basic"
          value={note}
          onChange={handleNoteChange}
          variant="outlined"
          placeholder="Additional details about transaction"
        />
        
        <TextField
          id="date"
          label="Date"
          type="date"
          onChange={handleDateChange}
          value={date}
          InputLabelProps={{
            shrink: true,
          }}
        />
        <button
          type="button"
          className="btn btn-outline-secondary"
          onClick={handleCancel}
        >
          Cancel
        </button>
        <button
          type="submit"
          className="btn btn-outline-success"
          onClick={handleSave}
        >
          Save
        </button>
      </div>
    </>
  );
};

export default Transaction;
