import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import MenuItem from "@mui/material/MenuItem";
import classes from "./css/wallet.module.css";
import Navbar from "./Navbar";
import axios from "axios";

const currencies = [
  {
    value: "USD",
    label: "$",
  },
  {
    value: "EUR",
    label: "€",
  },
  {
    value: "BTC",
    label: "฿",
  },
  {
    value: "JPY",
    label: "¥",
  },
  {
    value: "INR",
    label: "₹",
  },
];

const [currency, setCurrency] = useState("INR");
  const [walletName, setWalletName] = useState("");
  const [intialBalance, setIntialBalance] = useState(0);

  const handleWalletNameChange = (event) => {
    setWalletName(event.target.value);
  };

  const handleCurrencyChange = (event) => {
    setCurrency(event.target.value);
  };

  const handleIntialBalanceChange = (event) => {
    setIntialBalance(event.target.value);
  };
  const createWallet = () => {
    const walletData = {
      wallet_name: walletName,
      currency_type: currency,
      initial_balance: intialBalance,
      id: localStorage.getItem("id"),
    };

    console.log(walletData);

    axios
      .put("https://money-tracker-22.herokuapp.com/wallet", walletData)
      .then(function (response) {
        console.log(response.data);
        localStorage.setItem("loginData", JSON.stringify(response.data.data));
        console.log(response.data.message);
        console.log(response.data.data.id);
        localStorage.setItem("id", response.data.data.id);
        // if (response.data.message === "user added") {
        //   navigate("/wallet");
        // } else {
        //   navigate("/home");
        // }
        // localStorage.setItem('lastName',response.profileObj.familyName);
        // localStorage.setItem('fullName',response.profileObj.name);
      })
      .catch(function (error) {
        console.log(error);
      });
  };
  
const Wallet = () => {
  

  return (
    <div>
      <Navbar />
      <section>
        <div className="container">
          <div className={classes.box}>
            <div className={`row ${classes.p}`}>
              <div className="row my-2">
                <h4 className={classes.a}>Add a Wallet first!!!</h4>
                <hr></hr>
              </div>
              <div className="row my-3">
                <div className="col">
                  <TextField
                    id="outlined-basic"
                    label="Wallet name"
                    variant="outlined"
                    placeholder="Your wallet name"
                    value={walletName}
                    onChange={handleWalletNameChange}
                  />
                </div>
                <div className="row my-2 "></div>
                <div className="col-6 col-sm-5 ">
                  <TextField
                    id="outlined-select-currency"
                    select
                    label="Select"
                    value={currency}
                    onChange={handleCurrencyChange}
                    helperText="Please select your currency"
                  >
                    {currencies.map((option) => (
                      <MenuItem key={option.value} value={option.value}>
                        {option.label} {option.value}
                      </MenuItem>
                    ))}
                  </TextField>
                </div>
                <div className="col-6 col-sm-5">
                  <TextField
                    id="outlined-basic"
                    label="Initial Balance"
                    variant="outlined"
                    value={intialBalance}
                    onChange={handleIntialBalanceChange}
                  />
                </div>
                
              </div>
            </div>
          </div>
          <button type="button" onClick={createWallet()}>
                  Add Wallet
          </button>
        </div>
      </section>
    </div>
  );
};

export default Wallet;
