import React, { useEffect, useState } from "react";
import MenuItem from "@mui/material/MenuItem";
import TextField from "@mui/material/TextField";

const TrasactionFilter = () => {
  const filters = [
    {
      value: "Date",
    },
    {
      value: "Month",
    },
    {
      value: "Year",
    },
  ];

  const months = [
    { index: "01", value: "January" },
    { index: "02", value: "Febuary" },
    { index: "03", value: "March" },
    { index: "04", value: "April" },
    { index: "05", value: "May" },
    { index: "06", value: "June" },
    { index: "07", value: "July" },
    { index: "08", value: "August" },
    { index: "09", value: "September" },
    { index: "10", value: "October" },
    { index: "11", value: "November" },
    { index: "12", value: "December" },
  ];
  const [filterSelected, setFilterSelected] = useState("Month");
  const [yearsList, setYearsList] = useState([]);
  const [yearSelected, setYearSelected] = useState(new Date().getFullYear());
  const [monthSelected, setMonthSelected] = useState(months[new Date().getMonth()].value);
  const [dateSelected, setDateSelected] = useState("");
  useEffect(() => {
    // console.log(yearSelected, monthSelected);
    let currentYear = new Date().getFullYear()
    let listOfYears = []
    for(let i=0;i<10;i++)
    {
        listOfYears[i]=currentYear-i;
    }
    console.log(listOfYears);
    setYearsList(listOfYears)
  }, []);

  const handleFilterChange = (event) => {
    setFilterSelected(event.target.value);
    setMonthSelected(months[new Date().getMonth()].value)
    setYearSelected(new Date().getFullYear())
  };

  const handleDateChange = (event) =>{
      setDateSelected(event.target.value)
      handleSubmit();
  }
  const handleMonthChange = (event) =>{
    setMonthSelected(event.target.value)
    }

    const handleYearChange = (event) =>{
        setYearSelected(event.target.value)
        handleSubmit();
    }

    const handleSubmit = () =>{
        let url = "";
        if(filterSelected === "Date")
        {
            url = "https://money-tracker-22.herokuapp.com/"
        }
        else if(filterSelected === "Month")
        {
            url = "https://money-tracker-22.herokuapp.com/"
        }
        else
        {
            url = "https://money-tracker-22.herokuapp.com/"
        }

        axios
      .put("https://money-tracker-22.herokuapp.com/wallet", walletData)
      .then(function (response) {
        console.log(response.data);

        console.log(response.data.message);
        console.log(response.data.data.id);
        
      })
      .catch(function (error) {
        console.log(error);
      });
    }
  const secondFilter = () => {
    return (
      <>
        {filterSelected === "Date" ? (
          <div className="col-4 col-md-4 col-sm-12 mt-3">
            <TextField
            id="date"
            label="Date"
            type="date"
            helperText={"Please select the date of transaction"}
            onChange={handleDateChange}
            value={dateSelected}
            InputLabelProps={{
              shrink: true,
            }}
          />
          </div>
        ) : filterSelected === "Month" ? (
          <div className="col-4 col-md-4 col-sm-12 mt-3">
            <TextField
              id="outlined"
              select
              fullWidth
              label="Month Name"
              value={monthSelected}
              onChange={handleMonthChange}
              helperText="Select Month"
            >
              {Object.keys(months).map((monthIndex) => (
              <MenuItem
                key={monthIndex}
                value={months[monthIndex].value}
              >
                {months[monthIndex].value}
              </MenuItem>
            ))}
            </TextField>
          </div>
        ) : (
          <div className="col-4 col-md-4 col-sm-12 mt-3">
            <TextField
              id="outlined"
              select
              fullWidth
              label="Year"
              value={yearSelected}
              onChange={handleYearChange}
              helperText="Select Product name"
            >
              {Object.keys(yearsList).map((yearIndex) => (
              <MenuItem
                key={yearIndex}
                value={yearsList[yearIndex]}
              >
                {yearsList[yearIndex]}
              </MenuItem>
            ))}
            </TextField>
          </div>
        )}

        {filterSelected === "Month" ? (
          <div className="col-4 col-md-4 col-sm-12 mt-3">
            <TextField
              id="outlined"
              select
              fullWidth
              label="Year"
              value={yearSelected}
              onChange={handleYearChange}
              helperText="Select Product name"
            >
              {Object.keys(yearsList).map((yearIndex) => (
              <MenuItem
                key={yearIndex}
                value={yearsList[yearIndex]}
              >
                {yearsList[yearIndex]}
              </MenuItem>
            ))}
            </TextField>
          </div>
        ) : (
          <></>
        )}
      </>
    );
  };

  return (
    <div className="container p-5">
      <div className="row">
        <div className="col-4 col-md-4 col-sm-12 mt-3">
          <TextField
            id="outlined"
            select
            fullWidth
            label="Filter by"
            value={filterSelected}
            onChange={handleFilterChange}
          >
            {Object.keys(filters).map((filter) => (
              <MenuItem key={filter} value={filters[filter].value}>
                {filters[filter].value}
              </MenuItem>
            ))}
          </TextField>
        </div>
        {secondFilter()}
      </div>
    </div>
  );
};

export default TrasactionFilter;
