import React from "react";
import GoogleLogin from "react-google-login";
import axios from "axios";
import { useNavigate } from "react-router";
import classes from "./components/CSS/login.module.css";

const Login = () => {
  const navigate = useNavigate();
  const responseGoogle = (response) => {
    console.log(response);
    console.log(response.profileObj);

    const loginData = {
      firstname: response.profileObj.givenName,
      lastname: response.profileObj.familyName,
      fullname: response.profileObj.name,
      emailid: response.profileObj.email,
      platform: "Google",
    };
    axios
      .post("https://money-tracker-22.herokuapp.com/users", loginData)
      .then(function (response) {
        console.log(response.data);
        localStorage.setItem("loginData", JSON.stringify(loginData));
        navigate("/home");
        // localStorage.setItem('lastName',response.profileObj.familyName);
        // localStorage.setItem('fullName',response.profileObj.name);
      })
      .catch(function (error) {
        console.log(error);
      });
  };
  return (
    <div>
      {/* <h1>Login</h1> */}
      <section>
        <div className={classes.left}>
          <div>
            <h1>Money Tracker</h1>
          </div>
        </div>
        <div className={classes.right}>
          <div className={classes.content}>
            <h3>Log In</h3>
            <p>Using social networking accounts</p>

            <GoogleLogin
              clientId="108059489672-m06gsij5h4d92ll5f7pp59m81hokgm9j.apps.googleusercontent.com"
              // buttonText="Login with Google"
              onSuccess={responseGoogle}
              onFailure={responseGoogle}
              cookiePolicy={"single_host_origin"}
              // className={classes.login}
              render={(renderProps) => (
                <button type="button" className="btn btn-outline-danger">
                  <img src="./components/images/"></img>
                  Login with Google
                </button>
              )}
            >
              {/* <span>Login with google</span> */}
            </GoogleLogin>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Login;
