import React,{useState} from "react";
import TextField from "@mui/material/TextField";
import MenuItem from "@mui/material/MenuItem";
import classes from "./css/wallet.module.css";
import Navbar from './Navbar';


const currencies = [
  {
    value: "USD",
    label: "$",
  },
  {
    value: "EUR",
    label: "€",
  },
  {
    value: "BTC",
    label: "฿",
  },
  {
    value: "JPY",
    label: "¥",
  },
  {
    value: "INR",
    label: "₹",
  },
];


const Wallet = () => {
  const [currency, setCurrency] = useState("INR");
  const [walletName, setWalletName] = useState('');
  const [intialBalance, setIntialBalance] = useState(0);

  
  const handleWalletNameChange = (event) => {
    setWalletName(event.target.value);
    
  };

  const handleCurrencyChange = (event) => {
    setCurrency(event.target.value);
  };

  const handleIntialBalanceChange = (event) => {
    setIntialBalance(event.target.value);
  };
  const createWallet = () => {
    
  }

  return (
    <div>
      <Navbar/>
      <section>
        <div className="container">
          <div className={classes.box}>
            <div className={`row ${classes.p}`}>
              <div className="row my-2">
                <h4 className={classes.a}>Add a Wallet first!!!</h4>
                <hr></hr>
              </div>
              <div className="row my-3">
                <div className="col">
                  <TextField
                    id="outlined-basic"
                    label="Wallet name"
                    variant="outlined"
                    placeholder="Your wallet name"
                    value = {walletName}
                    onChange = {handleWalletNameChange}
                  />
                </div>
                <div className="row my-2 "></div>
                <div className="col-6 col-sm-5 ">
                  <TextField
                    id="outlined-select-currency"
                    select
                    label="Select"
                    value={currency}
                    onChange={handleCurrencyChange}
                    helperText="Please select your currency"
                  >
                    {currencies.map((option) => (
                      <MenuItem key={option.value} value={option.value}>
                        {option.label} {option.value}
                      </MenuItem>
                    ))}
                  </TextField>
                </div>
                <div className="col-6 col-sm-5">
                  <TextField
                    id="outlined-basic"
                    label="Initial Balance"
                    variant="outlined"
                    value = {intialBalance}
                    onChange = {handleIntialBalanceChange}
                  />
                </div>
                <button type="button" onClick={createWallet}>Add Wallet</button>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Wallet;
