import React,{useState} from 'react'
import Navbar from './components/Navbar'
import { Chart as ChartJS } from 'chart.js/auto'
import { Bar, Line } from "react-chartjs-2";
import ReportFilter from './components/ReportFilter';
// import { HorizontalBar } from 'react-chartjs-2';


const Report = () => {

    const [transactions, setTransactions] = useState({'message' : "data not found"});
    const data = {
        labels: ["Organic", "Sponsored", "Organic", "Sponsored", "Organic", "Sponsored", "Organic", "Sponsored", "Organic", "Sponsored", "Organic", "Sponsored", "Organic", "Sponsored", "Organic", "Sponsored", "Organic", "Sponsored", "Organic", "Sponsored", "Organic", "Sponsored", "Organic", "Sponsored", "Organic", "Sponsored", "Organic", "Sponsored"],
        previousDate: {
            // label: "08/10/2019 - 09/30/2019",
            dataSet: [10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000]
        },
        currentDate: {
            // label: "10/01/2019 - 11/20/2019",
            dataSet: [10000, 225000, 10000, 225000, -100000, 150000, 10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000, 10000, 150000]
        }
    };
    return (
        <div>
            <Navbar />
            <ReportFilter setTransactions={setTransactions}/>
            {filterSelected === "Date" ? (
          <div className="col-4 col-md-4 col-sm-12">
            <TextField
              id="date"
              label="Date"
              type="date"
              helperText={"Please select the date of transaction"}
              onChange={handleDateChange}
              value={dateSelected}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
        ) : filterSelected === "Month" ? (
          <div className="col-4 col-md-4 col-sm-12">
            <TextField
              id="outlined"
              select
              fullWidth
              label="Month Name"
              value={months[monthSelected].value}
              onChange={handleMonthChange}
              helperText="Select Month"
            >
              {Object.keys(months).map((monthIndex) => (
                <MenuItem key={monthIndex} value={months[monthIndex].value}>
                  {months[monthIndex].value}
                </MenuItem>
              ))}
            </TextField>
          </div>
        ) : (
          <div className="col-4 col-md-4 col-sm-12">
            <TextField
              id="outlined"
              select
              fullWidth
              label="Year"
              value={yearSelected}
              onChange={handleYearChange}
              helperText="Select Product name"
            >
              {Object.keys(yearsList).map((yearIndex) => (
                <MenuItem key={yearIndex} value={yearsList[yearIndex]}>
                  {yearsList[yearIndex]}
                </MenuItem>
              ))}
            </TextField>
          </div>
        )}

        {filterSelected === "Month" ? (
          <div className="col-4 col-md-4 col-sm-12">
            <TextField
              id="outlined"
              select
              fullWidth
              label="Year"
              value={yearSelected}
              onChange={handleYearChange}
              helperText="Select Product name"
            >
              {Object.keys(yearsList).map((yearIndex) => (
                <MenuItem key={yearIndex} value={yearsList[yearIndex]}>
                  {yearsList[yearIndex]}
                </MenuItem>
              ))}
            </TextField>
          </div>
        ) : (
          <></>
        )}
            <div className="container">
                <Line
                    pointstyle="star"
                    data={{
                        labels: data.labels,
                        responsive: true,
                        offset: true,
                        datasets: [
                            {
                                label: "Income",
                                pointStyle: "rectRounded",
                                backgroundColor: "#6ED3FF",
                                barThickness: 40,
                                categoryPercentage: 1,
                                borderColor: "#6ED3FF",
                                data: data.previousDate.dataSet //From API
                            },
                            {
                                label: "Expense",
                                backgroundColor: "#FF0000",
                                borderColor: "#FF0000",
                                barThickness: 40,
                                categoryPercentage: 1,
                                pointStyle: "triangle",
                                data: data.currentDate.dataSet //From API
                            }
                        ]
                    }}
                    height={400}
                    options={{
                        //  
                        offsetGridLines: true,
                        drawTicks: true,
                        layout: {
                            padding: {
                                top: 30,
                                right: 40,
                                bottom: 40
                            }
                        },
                        legend: {
                            display: true,
                            position: "right",
                            align: "start",
                            labels: {
                                usePointStyle: true
                            }
                        },
                        responsive: true,
                        maintainAspectRatio: false,
                        scales: {
                            xAxes:
                            {
                                stacked: true,
                                ticks: {
                                    padding: 5
                                },
                                gridLines: {
                                    display: false
                                }
                            },
                            yAxes: {
                                stacked: false,
                                gridLines: {
                                    drawBorder: false
                                },
                                ticks: {
                                    beginAtZero: true,
                                    maxTicksLimit: 6,
                                    padding: 20,
                                    callback(n) {
                                        if (n < 1e3) return n;
                                        if (n >= 1e3) return +(n / 1e3).toFixed(1) + "K";
                                    }
                                }
                            }
                        }
                    }}
                />
            </div>
            {/* <div className="container">
                <Bar
                    pointstyle="star"
                    data={{
                        labels: data.labels,
                        responsive: true,
                        offset: true,
                        datasets: [
                            {
                                label: "Mobile",
                                pointStyle: "rectRounded",
                                backgroundColor: "#6ED3FF",
                                barThickness: 40,
                                categoryPercentage: 1,
                                borderColor: "#6ED3FF",
                                data: data.previousDate.dataSet //From API
                            },
                            {
                                label: "Desktop",
                                backgroundColor: "#FF0000",
                                borderColor: "#FF0000",
                                barThickness: 40,
                                categoryPercentage: 1,
                                pointStyle: "triangle",
                                data: data.currentDate.dataSet //From API
                            }
                        ]
                    }}
                    height={400}
                    options={{
                        //  
                        offsetGridLines: true,
                        drawTicks: true,
                        layout: {
                            padding: {
                                top: 30,
                                right: 40,
                                bottom: 40
                            }
                        },
                        legend: {
                            display: true,
                            position: "right",
                            align: "start",
                            labels: {
                                usePointStyle: true
                            }
                        },
                        responsive: true,
                        maintainAspectRatio: false,
                        scales: {
                            xAxes:
                            {
                                stacked: true,
                                ticks: {
                                    padding: 5
                                },
                                gridLines: {
                                    display: false
                                }
                            },
                            yAxes: {
                                stacked: false,
                                gridLines: {
                                    drawBorder: false
                                },
                                ticks: {
                                    beginAtZero: true,
                                    maxTicksLimit: 6,
                                    padding: 20,
                                    callback(n) {
                                        if (n < 1e3) return n;
                                        if (n >= 1e3) return +(n / 1e3).toFixed(1) + "K";
                                    }
                                }
                            }
                        }
                    }}
                />
            </div> */}
        </div>
    )
}
export default Report
