import React, { useEffect, useState } from "react";
import MenuItem from "@mui/material/MenuItem";
import TextField from "@mui/material/TextField";

const TrasactionFilter = () => {
  const [filterSelected, setFilterSelected] = useState("Month");
  const [yearsList, setYearsList] = useState([]);
  const [currentYear, setCurrentYear] = useState(new Date().getFullYear());
  const [currentMonth, setCurrentMonth] = useState(new Date().getMonth());



  const filters = [
    {
      value: "Date",
    },
    {
      value: "Month",
    },
    {
      value: "Year",
    },
  ];

  const months = [
    {index:"01",value:"January"},
    {index:"02",value:"January"},
    {index:"03",value:"January"},
    {index:"04",value:"January"},
    {index:"05",value:"January"},
    {index:"06",value:"January"},
    {index:"07",value:"January"},
    {index:"08",value:"January"},
    {index:"09",value:"September"},
    {index:"10",value:"October"},
    {index:"11",value:"November"},
    {index:"12",value:"December"}

  ]

  useEffect(() => {
    
  console.log(currentYear,currentMonth);
    
  }, []);
  
  const handleFilterChange = (event) => {
    setFilterSelected(event.target.value);
  };

  const secondFilter = () => {
    return (
      <>
        {filterSelected === "Date" ? (
          <div className="col-4 col-md-4 col-sm-12 mt-3">
            <TextField
              id="outlined"
              select
              fullWidth
              label="Date"
              // value={productName}
              // onChange={handleProductChange}
              helperText="Select Date"
            >
              {/* {Object.keys(productsData).map((product) => (
              <MenuItem
                key={productsData[product].prodId}
                value={productsData[product].prodName}
              >
                {productsData[product].prodName}
              </MenuItem>
            ))} */}
            </TextField>
          </div>
        ) : filterSelected === "Month" ? (
          <div className="col-4 col-md-4 col-sm-12 mt-3">
            <TextField
              id="outlined"
              select
              fullWidth
              label="Month Name"
              // value={productName}
              // onChange={handleProductChange}
              helperText="Select Month"
            >
              {/* {Object.keys(productsData).map((product) => (
              <MenuItem
                key={productsData[product].prodId}
                value={productsData[product].prodName}
              >
                {productsData[product].prodName}
              </MenuItem>
            ))} */}
            </TextField>
          </div>
        ) : (
          <div className="col-4 col-md-4 col-sm-12 mt-3">
            <TextField
              id="outlined"
              select
              fullWidth
              label="Year"
              // value={productName}
              // onChange={handleProductChange}
              helperText="Select Year"
            >
              {/* {Object.keys(productsData).map((product) => (
              <MenuItem
                key={productsData[product].prodId}
                value={productsData[product].prodName}
              >
                {productsData[product].prodName}
              </MenuItem>
            ))} */}
            </TextField>
          </div>
        )}

        {filterSelected === "Month" ? (
          <div className="col-4 col-md-4 col-sm-12 mt-3">
            <TextField
              id="outlined"
              select
              fullWidth
              label="Year"
              // value={productName}
              // onChange={handleProductChange}
              helperText="Select Product name"
            >
              {/* {Object.keys(productsData).map((product) => (
              <MenuItem
                key={productsData[product].prodId}
                value={productsData[product].prodName}
              >
                {productsData[product].prodName}
              </MenuItem>
            ))} */}
            </TextField>
          </div>
        ) : (
          <></>
        )}
      </>
    );
  };

  return (
    <div className="container p-5 border">
      <div className="row">
        <div className="col-4 col-md-4 col-sm-12 mt-3">
          <TextField
            id="outlined"
            select
            fullWidth
            label="Filter by"
            value={filterSelected}
            onChange={handleFilterChange}
          >
            {Object.keys(filters).map((filter) => (
              <MenuItem key={filter} value={filters[filter].value}>
                {filters[filter].value}
              </MenuItem>
            ))}
          </TextField>
        </div>
        {secondFilter()}
      </div>
    </div>
  );
};

export default TrasactionFilter;
