import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import MenuItem from "@mui/material/MenuItem";
import axios from "axios";
// import DatePicker from "react-datepicker";
// import "react-datepicker/dist/react-datepicker.css";

const Transaction = () => {
  const expenseCategory = [
    {
      value: "Food",
      label: "$",
    },
    {
      value: "Transport",
      label: "€",
    },
  ];
  const incomeCategory = [
    {
      value: "Salary",
      label: "$",
    },
    {
      value: "Other Income",
      label: "€",
    },
  ];
  const types = [
    {
      value: "Income",
      label: "+",
    },
    {
      value: "Expense",
      label: "-",
    },
  ];

  const [type, setType] = useState("Income");
  const [category, setCategory] = useState("");
  const [amount, setAmount] = useState(0);
  const [note, setNote] = useState("");
  const [date, setDate] = useState("");
  console.log(date);
  // console.log(new Date().toLocaleDateString("en-GB"));
  const handleTypeChange = (event) => {
    setType(event.target.value);
  };
  const handleCategoryChange = (event) => {
    setCategory(event.target.value);
  };
  const handleAmountChange = (event) => {
    setAmount(event.target.value);
  };
  const handleNoteChange = (event) => {
    setNote(event.target.value);
  };
  const handleDateChange = (event) => {
    const newDate = event.target.value.split("-").reverse().join("/");
    console.log(newDate);
    setDate(newDate);
  };
  const handleCancel = () => {
    setType("Income");
    setCategory("");
    setAmount(0);
    setNote("");
    setDate("");
  };
  const handleSave = () => {};
  return (
    <div className="container">
      <TextField
        id="outlined-select-currency"
        select
        label="Type"
        helperText="Please select your type"
        onChange={handleTypeChange}
        value={type}
      >
        {types.map((option) => (
          <MenuItem key={option.value} value={option.value}>
            {option.label} {option.value}
          </MenuItem>
        ))}
      </TextField>

      {type === "Income" ? (
        <TextField
          id="outlined-select-currency"
          select
          label="Category"
          helperText="Please select your category"
          value={category}
          onChange={handleCategoryChange}
        >
          {incomeCategory.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label} {option.value}
            </MenuItem>
          ))}
        </TextField>
      ) : (
        <TextField
          id="outlined-select-currency"
          select
          label="Category"
          helperText="Please select your category"
          value={category}
          onChange={handleCategoryChange}
        >
          {expenseCategory.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label} {option.value}
            </MenuItem>
          ))}
        </TextField>
      )}

      <TextField
        id="outlined-basic"
        label="Amount"
        value={amount}
        onChange={handleAmountChange}
        variant="outlined"
      />
      <TextField
        fullWidth
        label="Note"
        id="outlined-basic"
        value={note}
        onChange={handleNoteChange}
        variant="outlined"
        placeholder="Additional details about transaction"
      />
      {/* <TextField
        fullWidth
        label="Date"
        id="outlined-basic"
        value={date}
        onChange={handleDateChange}
        variant="outlined"
        placeholder="Date"
      /> */}
      {/* <DatePicker
        dateFormat="dd/MM/yyyy"
        selected={date}
        
        value={date}
        onChange={(date) => setDate(date.toLocaleDateString('en-GB'))}
      /> */}

      {/* <input type="date" value={date} onChange={handleDateChange} /> */}
      <TextField
        id="date"
        label="Date"
        type="date"
        onChange={handleDateChange}
        value={date}
        InputLabelProps={{
          shrink: true,
        }}
      />
      <button
        type="button"
        className="btn btn-outline-secondary"
        onClick={handleCancel}
      >
        Cancel
      </button>
      <button
        type="button"
        className="btn btn-outline-success"
        onClick={handleSave}
      >
        Save
      </button>
    </div>
  );
};

export default Transaction;
